package tutorial_3;

public class if_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int test = 6;
		
		if (test == 6) {
			System.out.println(test);
			test++;
			System.out.println(test);
			System.out.println(test++);
			System.out.println(test);
		}else {
			System.out.println(test);
			++test;
			System.out.println(test);
			
		}

	}

}
