package tutorial_3;

public class increment_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int tuna = 5;
		int bass = 18;
		
		System.out.println(tuna); // before post increment = 5
		
		//Prefix - pre increment changes the value before the use
		++tuna;  
		System.out.println(tuna); 
		
		tuna += 8;
		
		//postfix change variable after it has been used
		//tuna++; //after post increment 6
		//System.out.println(tuna); 
		
		//post increment always after used of variable
		//while pre incremting always before the used of variable changed value
		
		
		

	}

}
