package tutorial_3;

public class mathLibrary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.abs(-10.45555));
		System.out.println(Math.ceil(10.45555)); // up the number
		System.out.println(Math.floor(10.45555)); // down the number
		System.out.println(Math.max(-10.45555, -0.4545));
		System.out.println(Math.min(-10.45555, -0.4545));
		System.out.println(Math.pow(2,3));
		System.out.println(Math.sqrt(2));
	}

}
